import React, { Component } from "react";
import ProgrammersCard from "./ProgrammersCard";
import Info from "./memberInfo";

class ProgrammersGrid extends Component {
  constructor() {
    super();
    this.state = {
      info: Info,
      number: 0,
      newList: [],
    };
    this.addTOArray = this.addTOArray.bind(this);
    this.mapping = this.mapping.bind(this);
  }
  addTOArray() {
    const { number, newList, info } = this.state;
    if (number < info.length) {
      this.setState((prev) => {
        return {
          number: prev.number + 1,
          newList: [...prev.newList, info[prev.number]],
        };
      });
      console.log(`new List ${number}`, newList);
      // return this.mapping(newList);
      // const members = newList.map((member) => (
      //   <ProgrammersCard
      //     key={member.id}
      //     name={member.name}
      //     jop={member.jop}
      //     phone={member.phone}
      //     image={member.image}
      //     website={member.website}
      //   />
      // ));
      // return members;
       members = newList.map((member) => (
        <ProgrammersCard
          key={member.id}
          name={member.name}
          jop={member.jop}
          phone={member.phone}
          image={member.image}
          website={member.website}
        />
      ));
    }
  }
  mapping(x) {
    const members = x.map((member) => (
      <ProgrammersCard
        key={member.id}
        name={member.name}
        jop={member.jop}
        phone={member.phone}
        image={member.image}
        website={member.website}
      />
    ));
    return members;
  }

  render() {
    return (
      <div className="row justify-content-md-center ms-6">
        <button
          type="button"
          style={{ marginBottom: "25px" }}
          className="btn btn-info"
          onClick={this.addTOArray}
        >
          {"click to add member"}...................................
        </button>
        {/* {this.mapping(this.state.newList)} */}
        {this.members }
      </div>
    );
  }
}
export default ProgrammersGrid;
