import React, { Component } from "react";
import ProgrammersCard from "./ProgrammersCard";
import Info from "./memberInfo";
import ListButton from "./ListButton";
class ProgrammersGrid extends Component {
  constructor() {
    super();
    this.state = {
      info:Info,
      number:0,
      newArray:[]
      };
  }
  addTOArray(){
    const {number,newList,info}=this.state;
    if (number!==info.length)
   { this.setState((prev) => {
        return { number: prev.number +1 ,
          newList:[...prev.newList,info[prev.number]]};
      });
return this.mapping(newList);

    }
  }
  mapping(x) {
    const members = x.map((member) => (
      <ProgrammersCard
        key={member.id}
        name={member.name}
        jop={member.jop}
        phone={member.phone}
        image={member.image}
        website={member.website}
      />
    ));
    return members;
  }

  render() {
    return (
      <div className="row justify-content-md-center ms-6">
       
        <button type="button" class="btn btn-info" onClick={this.addTOArray}>
          {"hi"}...................................
        </button>
        {this.addTOArray(this.addTOArray())}

      </div>
    );
  }
}
export default ProgrammersGrid;
