import React from 'react';
import {  Title, SectionWrapper } from "../../components/index";
im
const FeaturesSection = () => {
  
  const features = [
    {
      title: "Easy Search",
      description: "Find your perfect car with our advanced search tools.",
      icon: "üîç", 
    },
    {
      title: "Exclusive Deals",
      description: "Get access to special offers and discounts.",
      icon: "üíé", 
    },
    {
      title: "24/7 Support",
      description: "We‚Äôre here to help you anytime, anywhere.",
      icon: "üìû",
    },
  ];

  return (
    <SectionWrapper> 
       <Title titlePrimary="Why Choose our " titleSecondary="  website" />
      <div style={styles.featuresContainer}>
        {features.map((feature, index) => (
          <div key={index} style={styles.featureCard}>
            <div style={styles.icon}>{feature.icon}</div>
            <h3 style={styles.featureTitle}>{feature.title}</h3>
            <p style={styles.featureDescription}>{feature.description}</p>
          </div>
        ))}
      </div>
      </SectionWrapper>
  );
};

const styles = {
 
  heading: {
    fontSize: "2rem",
    color: "#1E3A8A",
    marginBottom: "40px",
  },
  featuresContainer: {
    display: "flex",
    justifyContent: "space-around",
    flexWrap: "wrap",
    gap: "20px",
  },
  // featureCard: {
  //   backgroundColor: "#ffffff",
  //   padding: "20px",
  //   borderRadius: "10px",
  //   boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  //   maxWidth: "300px",
  //   flex: "1 1 300px",
  // }
  // ,
  icon: {
    fontSize: "2rem",
    marginBottom: "10px",
  },
  featureTitle: {
    fontSize: "1.5rem",
    color: "#2D3748",
    marginBottom: "10px",
  },
  featureDescription: {
    fontSize: "1rem",
    color: "#4A5568",
  },
};

export default FeaturesSection;
