import React, { Component } from "react";
import ProgrammersCard from "./ProgrammersCard";
import Info from "./memberInfo";

class ProgrammersGrid extends Component {
  constructor() {
    super();
    this.state = {
      info: Info,
      number: 0,
      newList: [],
    };
    this.addTOArray = this.addTOArray.bind(this);
    // this.mapping = this.mapping.bind(this);
  }
  addTOArray() {
    const { number, newList, info } = this.state;
    if (number < info.length) {
      this.setState((prev) => {
        return {
          number: prev.number + 1,
          newList: [...prev.newList, info[prev.number]],
        };
      });
      console.log(`new List ${number}`, newList);
      // return this.mapping(newList);
