      //     phone={member.phone}
      //     image={member.image}
      //     website={member.website}
      //   />
      // ));
      // return members;
       this.members = newList.map((member) => {
       return  <ProgrammersCard
          key={member.id}
          name={member.name}
          jop={member.jop}
          phone={member.phone}
          image={member.image}
          website={member.website}
        />
    });
    }
  }
  // mapping(x) {
  //   const members = x.map((member) => (
  //     <ProgrammersCard
  //       key={member.id}
  //       name={member.name}
  //       jop={member.jop}
  //       phone={member.phone}
  //       image={member.image}
  //       website={member.website}
  //     />
  //   ));
  //   return members;
  // }

  render() {
    return (
      <div className="row justify-content-md-center ms-6">
        <button
          type="button"
          style={{ marginBottom: "25px" }}
          className="btn btn-info"
          onClick={this.addTOArray}
        >
          {"click to add member"}...................................
        </button>
        {/* {this.mapping(this.state.newList)} */}
        {this.members }
      </div>
    );
  }
}
export default ProgrammersGrid;
